css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;

}
.print-format{
    padding: 12px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{% set c %}{% endset %}
{% if doc.quality_inspection%}
    {%- set c=frappe.get_doc("Quality Inspection",doc.quality_inspection) -%}
{% endif %}
{% set b %}{% endset %}
{% if doc.quality_inspection%}
    {%- set b=frappe.get_doc("Quality Inspection Template",{"quality_inspection_template_name":c.quality_inspection_template}) -%}
{% endif %}
<table>
    <tr>
    <td>

    {% if letter_head %}
        {{ letter_head }}
    {% endif %}

    </td>
    </tr>
</table>
<table>
    <tr>
        <td>
            <br>

            <div style="font-size: 16px;text-align: center; ">
                <b>CERTIFICATE OF ANALYSIS</b>
            </div>
            <br>
        </td>
    </tr>
</table>
<div>
{% if doc.expiry_date %}
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Product") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.item_name %}
                            {{doc.item_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Type") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_doctype %}
                            {{doc.reference_doctype}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Name") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_name %}
                            {{doc.reference_name}}
                        {% endif %}
                    </div>
                </div>
            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("BATCH NO") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.name %}
                            {{doc.name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date of Mfg.") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.manufacturing_date %}
                                    {{ doc.get_formatted("manufacturing_date") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date of Exp.") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.expiry_date %}
                                    {{ doc.get_formatted("expiry_date") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Quantity") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;{% if doc.batch_qty %}
                            {{doc.batch_qty}}
                        {% endif %}
                    </div>
                </div>
            </td>
        </tr>
    </table>
{% endif %}
{% if not doc.expiry_date %}
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Product") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.item_name %}
                            {{doc.item_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Type") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_doctype %}
                            {{doc.reference_doctype}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Name") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_name %}
                            {{doc.reference_name}}
                        {% endif %}
                    </div>
                </div>
            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("BATCH NO") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.name %}
                            {{doc.name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date of Mfg.") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.manufacturing_date %}
                                    {{ doc.get_formatted("manufacturing_date") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Quantity") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;{% if doc.batch_qty %}
                            {{doc.batch_qty}}
                        {% endif %}
                    </div>
                </div>
            </td>
        </tr>
    </table>
{% endif %}
     <table>
                <tr>
                    <th style="width: 5%;"><b>{{ _("Sr No.") }}</b></th>
                    <th style="width: 30%;"><b>{{ _("Test Parameters") }}</b></th>
                    <th style="width: 40%;"><b>{{ _("Specification") }}</b></th>
                    <th style="width: 15%;text-align:center;"><b>{{ _("Result") }}</b></th>
                    <th style="width: 10%;text-align:center;"><b>{{ _("Remark") }}</b></th>
                </tr>
                {% for i in doc.test_result %}
                    {% set e %}{% endset %}
                    {% if i.specification%}
                        {%- set e=frappe.get_doc("Quality Inspection Parameter",{"name":i.specification}) -%}
                    {% endif %}
                    <tr>
                        <td>{{loop.index}}</td>
                        <td>
                            {% if i.specification %}
                                {{i.specification}}
                            {% endif %}
                        </td>
                          <td>
                        {% if i.min_value>0 %}
                            between {{i.min_value}} and {{i.max_value}}
                        {% elif i.acceptance_formula %}
                            {{i.acceptance_formula}}
                        {% elif i.value %}
                            {{i.value}}
                        {% else %}
                        {% for j in b.item_quality_inspection_parameter %}
                            {%if i.specification==j.specification%}
                                {% if j.values%}
                                    {{j.values}}
                                {% endif %}
                            {% endif %}
                        {% endfor %}

                        {% endif %}
                </td>
                        <td>
                    {% if i.reading_1 %}
                        {% if i.reading_1 %}
                        <b></b> {{i.reading_1}}<br>
                        {% endif %}
                        {% if i.reading_2 %}
                        <b>reading2:</b> {{i.reading_2}}<br>
                        {% endif %}
                        {% if i.reading_3 %}
                        <b>reading3:</b> {{i.reading_3}}<br>
                        {% endif %}
                        {% if i.reading_4 %}
                        <b>reading4:</b> {{i.reading_4}}<br>
                        {% endif %}
                        {% if i.reading_5 %}
                        <b>reading5:</b> {{i.reading_5}}<br>
                        {% endif %}
                        {% if i.reading_6 %}
                        <b>reading6:</b> {{i.reading_6}}<br>
                        {% endif %}
                        {% if i.reading_7 %}
                        <b>reading7:</b> {{i.reading_7}}<br>
                        {% endif %}
                        {% if i.reading_8 %}
                        <b>reading8:</b> {{i.reading_8}}<br>
                        {% endif %}
                        {% if i.reading_9 %}
                        <b>reading9:</b> {{i.reading_9}}<br>
                        {% endif %}
                        {% if i.reading_10 %}
                        <b>reading10:</b> {{i.reading_10}}<br>
                        {% endif %}
                    {% elif i.reading_value %}
                        {{i.reading_value}}
                    {% else %}
                        {{i.parameter_value}}
                    {% endif %}
                        </td>
                        <td>
                            {% if i.status %}
                                {{i.status}}
                            {% endif %}
                        </td>
                    </tr>
                {% endfor %}

            </table>
    <table>
        <tr>
            <td style="width: 80%;">
                <b>
                    The product confirms to the specification and is of standard quality.
                </b>
            </td>
            <td style="width: 20%; text-align: center; ">
                <br><br>
                {% if doc.owner %}
                    {{doc.owner}}
                {% endif %}<br>
                Tested by:QC<br>
                    Department
            </td>
        </tr>
    </table>
</div>

<div style="font-size: 12px;">
    This is computer generated Certificate of Analysis, hence no signature is required.
</div>
