css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;

}
.print-format{
    padding: 12px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{%- for item in doc.items -%}
    {%- if item.batch_no -%}

        {% set batch %}{% endset %}
        {% if item.batch_no %}
            {%- set batch=frappe.get_doc("Batch",item.batch_no) -%}
        {% endif %}
        {% set c %}{% endset %}
        {% if batch.quality_inspection%}
            {%- set c=frappe.get_doc("Quality Inspection",batch.quality_inspection) -%}
        {% endif %}
        {% set b %}{% endset %}
        {% if doc.quality_inspection%}
            {%- set b=frappe.get_doc("Quality Inspection Template",{"quality_inspection_template_name":c.quality_inspection_template}) -%}
        {% endif %}
        {% set sta %}{% endset %}
        {% if item.sales_order and doc.customer%}
            {%- set sta=frappe.get_doc("Customer Quality Inspection",{"sales_order":item.sales_order,"customer":doc.customer}) -%}
        {% endif %}
        <table>
            <tr>
                <td>
                    {% if letter_head %}
                        {{ letter_head }}
                    {% endif %}

                </td>
            </tr>
        </table>
        <table>
            <tr>
                <td>
                    <br>

                    <div style="font-size: 16px;text-align: center; ">
                        <b>CERTIFICATE OF ANALYSIS</b>
                    </div>
                    <br>
                </td>
            </tr>
        </table>
        <div>
        {% if batch.expiry_date %}
            <table>
                <tr>
                    <td style="width: 50%;">
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Product") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if item.item_name %}
                                    {{item.item_name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Customer Name") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.customer_name %}
                                    {{doc.customer_name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Invoice No") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.name %}
                                    {{doc.name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Dated") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.posting_date %}
                                    {{doc.get_formatted("posting_date")}}
                                {% endif %}
                            </div>
                        </div>
                    </td>
                    <td style="width: 50%;">
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("BATCH NO") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if item.batch_no %}
                                    {{item.batch_no}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Date of Mfg.") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;{% if batch.manufacturing_date %}
                                            {{ batch.get_formatted("manufacturing_date") }}
                                        {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Date of Exp.") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;{% if batch.expiry_date %}
                                            {{ batch.get_formatted("expiry_date") }}
                                        {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Quantity") }}</b>
                            </div>
                            <div class="col-xs-8">
                                <!--:&nbsp;{% if item.total_weight %}{{item.total_weight}}{% endif %}&nbsp;{% if item.weight_uom %}{{item.weight_uom}}{% endif %}-->
                                 :&nbsp;{% if item.qty %}
                                    {{item.qty}}
                                {% endif %}
                            </div>
                        </div>
                    </td>
                </tr>
            </table>
            {% endif %}
            {% if not batch.expiry_date %}
            <table>
                <tr>
                    <td style="width: 50%;">
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Product") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if item.item_name %}
                                    {{item.item_name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Customer Name") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.customer_name %}
                                    {{doc.customer_name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Invoice No") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.name %}
                                    {{doc.name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Dated") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.posting_date %}
                                    {{doc.get_formatted("posting_date")}}
                                {% endif %}
                            </div>
                        </div>
                    </td>
                    <td style="width: 50%;">
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("BATCH NO") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if item.batch_no %}
                                    {{item.batch_no}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Date of Mfg.") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;{% if batch.manufacturing_date %}
                                            {{ batch.get_formatted("manufacturing_date") }}
                                        {% endif %}
                            </div>
                        </div>
                        <!--<div class="row">-->
                        <!--    <div class="col-xs-4">-->
                        <!--        <b>{{ _("Date of Exp.") }}</b>-->
                        <!--    </div>-->
                        <!--    <div class="col-xs-8">-->
                        <!--        :&nbsp;{% if batch.expiry_date %}-->
                        <!--                    {{ batch.get_formatted("expiry_date") }}-->
                        <!--                {% endif %}-->
                        <!--    </div>-->
                        <!--</div>-->
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Quantity") }}</b>
                            </div>
                            <div class="col-xs-8">
                                <!--:&nbsp;{% if item.total_weight %}{{item.total_weight}}{% endif %}&nbsp;{% if item.weight_uom %}{{item.weight_uom}}{% endif %}-->
                                 :&nbsp;{% if item.qty %}
                                    {{item.qty}}
                                {% endif %}
                            </div>
                        </div>
                    </td>
                </tr>
            </table>
            {% endif %}
            {%if sta%}
            <table>
                <tr>
                    <th style="width: 5%;"><b>{{ _("Sr No.") }}</b></th>
                    <th style="width: 35%;"><b>{{ _("Test Parameters") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Specification") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Result") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Remark") }}</b></th>
                </tr>
                {% for i in batch.test_result %}
                    {%for j in sta.item_quality_inspection_parameter%}
                    {%if j.specification==i.specification %} 
                    <tr>
                        <td>{{j.idx}}</td>
                        <td>
                            {% if i.specification==j.specification %}
                                {{j.specification}}
                            {% endif %}
                        </td>
                        <td>
                        {% if i.min_value==j.min_value %}
                            between {{j.min_value}} and {{j.max_value}}
                        {% elif j.acceptance_formula==i.acceptance_formula %}
                            {{j.acceptance_formula}}
                        {% elif i.value==j.value %}
                            {{j.value}}
                        {% else %}
                            {% if i.values==j.values %}
                                {{j.values}}
                            {% endif %}
                        {% endif %}
                        </td>
                        <td>
                        {%if i.specification==j.specification%}
                        {% if i.reading_1 %}
                        {% if i.reading_1 %}
                        <b>reading1:</b> {{i.reading_1}}<br>
                        {% endif %}
                        {% if i.reading_2 %}
                        <b>reading2:</b> {{i.reading_2}}<br>
                        {% endif %}
                        {% if i.reading_3 %}
                        <b>reading3:</b> {{i.reading_3}}<br>
                        {% endif %}
                        {% if i.reading_4 %}
                        <b>reading4:</b> {{i.reading_4}}<br>
                        {% endif %}
                        {% if i.reading_5 %}
                        <b>reading5:</b> {{i.reading_5}}<br>
                        {% endif %}
                        {% if i.reading_6 %}
                        <b>reading6:</b> {{i.reading_6}}<br>
                        {% endif %}
                        {% if i.reading_7 %}
                        <b>reading7:</b> {{i.reading_7}}<br>
                        {% endif %}
                        {% if i.reading_8 %}
                        <b>reading8:</b> {{i.reading_8}}<br>
                        {% endif %}
                        {% if i.reading_9 %}
                        <b>reading9:</b> {{i.reading_9}}<br>
                        {% endif %}
                        {% if i.reading_10 %}
                        <b>reading10:</b> {{i.reading_10}}<br>
                        {% endif %}
                    {% elif i.reading_value %}
                        {{i.reading_value}}
                    {% else %}
                        {{i.parameter_value}}
                    {% endif %}
                        </td>
                        <td>
                            {% if i.status %}
                                {{i.status}}
                            {% endif %}
                        </td>
                    </tr>
                    {% endif %}
                {% endif %}
                {% endfor %}
            {% endfor %}
            </table>
            {%else %}
            <table>
                <tr>
                    <th style="width: 5%;"><b>{{ _("Sr No.") }}</b></th>
                    <th style="width: 35%;"><b>{{ _("Test Parameters") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Specification") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Result") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Remark") }}</b></th>
                </tr>
                {% for i in batch.test_result %}
                    <tr>
                        <td>{{i.idx}}</td>
                        <td>
                            {% if i.specification %}
                                {{i.specification}}
                            {% endif %}
                        </td>
                        <td>
                        {% if i.min_value>0 %}
                            between {{i.min_value}} and {{i.max_value}}
                        {% elif i.acceptance_formula %}
                            {{i.acceptance_formula}}
                        {% elif i.value %}
                            {{i.value}}
                        {% else %}
                           {% for i in b.item_quality_inspection_parameter %}
                            {% if i.values  %}
                                {{i.values}}
                            {% endif %}
                            {% endfor %}
                        {% endif %}
                        </td>
                        <td>
                        {% if i.reading_1 %}
                        {% if i.reading_1 %}
                        <b>reading1:</b> {{i.reading_1}}<br>
                        {% endif %}
                        {% if i.reading_2 %}
                        <b>reading2:</b> {{i.reading_2}}<br>
                        {% endif %}
                        {% if i.reading_3 %}
                        <b>reading3:</b> {{i.reading_3}}<br>
                        {% endif %}
                        {% if i.reading_4 %}
                        <b>reading4:</b> {{i.reading_4}}<br>
                        {% endif %}
                        {% if i.reading_5 %}
                        <b>reading5:</b> {{i.reading_5}}<br>
                        {% endif %}
                        {% if i.reading_6 %}
                        <b>reading6:</b> {{i.reading_6}}<br>
                        {% endif %}
                        {% if i.reading_7 %}
                        <b>reading7:</b> {{i.reading_7}}<br>
                        {% endif %}
                        {% if i.reading_8 %}
                        <b>reading8:</b> {{i.reading_8}}<br>
                        {% endif %}
                        {% if i.reading_9 %}
                        <b>reading9:</b> {{i.reading_9}}<br>
                        {% endif %}
                        {% if i.reading_10 %}
                        <b>reading10:</b> {{i.reading_10}}<br>
                        {% endif %}
                    {% elif i.reading_value %}
                        {{i.reading_value}}
                    {% else %}
                        {{i.parameter_value}}
                    {% endif %}
                        </td>
                        <td>
                            {% if i.status %}
                                {{i.status}}
                            {% endif %}
                        </td>
                    </tr>
                {% endfor %}

            </table>
        {% endif %}
            <table>
                <tr>
                    <td style="width: 80%;">
                        <b>
                            The product confirms to the specification and is of standard quality.
                        </b>
                    </td>
                    <td style="width: 20%; text-align: center; ">
                        <br><br>
                        {% if batch.owner %}
                            {{batch.owner}}
                        {% endif %}<br>
                        Tested by
                    </td>
                </tr>
            </table>
        </div>

        <div style="font-size: 12px;">
            This is computer generated Certificate of Analysis, hence no signature is required.
        </div>
        <div class="page-break"></div>
    {%- endif -%}
{%- endfor -%}
