css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:8px;

}
.print-format{
    padding: 5px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{% set b %}{% endset %}
{% if doc.quality_inspection%}
    {%- set b=frappe.get_doc("Quality Inspection Template",{"quality_inspection_template_name":doc.quality_inspection_template}) -%}
{% endif %}
<table>
    <tr>
    <td>

    {% if letter_head %}
        {{ letter_head }}
    {% endif %}

    </td>
    </tr>
</table>
<table>
    <tr>
        <td>
            <br>

            <div style="font-size: 16px;text-align: center; ">
                <b>RAW MATERIAL INTERNAL ANALYSIS REPORT</b>
                </div>
            <br>
        </td>
    </tr>
</table>
<div>
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Product Name") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                         {% if doc.item_name %}
                            {{doc.item_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Product") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                         {% if doc.item_code %}
                            {{doc.item_code}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Type") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_type %}
                            {{doc.reference_type}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Name") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_name %}
                            {{doc.reference_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Date") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.date %}
                            {{doc.date}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("SCIPL Batch No") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.batch_no %}
                            {{doc.batch_no}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Packaging Details") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;{% if doc.packing_details %}
                            {{doc.packing_details}}
                        {% endif %}
                    </div>
                    </div>
               </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Supplier Batch No") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.supplier %}
                            {{doc.supplier}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Supplier") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.supplier_name %}
                                    {{ doc.get_formatted("supplier_name") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Manufacturer") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.manufacturer_name %}
                            {{doc.manufacturer_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date of Sampling") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.date_of_sampling %}
                                    {{ doc.get_formatted("date_of_sampling") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Sample Quantity") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;{% if (doc.sample_size),(doc.uom) %}
                            {{doc.sample_size}},{{doc.uom}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Sampled By") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.supplier %}
                            {{doc.supplier}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date Of Inspection") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;{% if doc.report_date %}
                                    {{ doc.get_formatted("report_date") }}
                                {% endif %}
                    </div>
                </div>
                <br>
            </td>
        </tr>
    </table>
    <table>
        <tr>
            <th style="width: 5%;"><b>{{ _("Sr No.") }}</b></th>
            <th style="width: 30%;"><b>{{ _("Test Parameters") }}</b></th>
            <th style="width: 43%;"><b>{{ _("Specification") }}</b></th>
            <th style="width: 15%;"><b>{{ _("Result") }}</b></th>
            <th style="width: 12%;"><b>{{ _("Remark") }}</b></th>
        </tr>
        {% for i in doc.readings %}
            <tr>
                <td>{{i.idx}}</td>
                <td>
                    {% if i.specification %}
                        {{i.specification}}
                    {% endif %}
                </td>
                 <td>
                        {% if i.min_value>0 or i.max_value>0 %}
                                    between {{i.min_value}} and {{i.max_value}}
                                {% elif i.description %}
                                    {{i.description}}
                                {% elif i.values %}
                                    {{i.values}}
                                {%else %}
                                    {% if i.value %}
                                    {{i.value}}
                                    {% endif %}  
                                {% endif %}
                </td>
                <td>
                    {% if i.reading_1 %}
                        {% if i.reading_1 %}
                        <b>reading1:</b> {{i.reading_1}}<br>
                        {% endif %}
                        {% if i.reading_2 %}
                        <b>reading2:</b> {{i.reading_2}}<br>
                        {% endif %}
                        {% if i.reading_3 %}
                        <b>reading3:</b> {{i.reading_3}}<br>
                        {% endif %}
                        {% if i.reading_4 %}
                        <b>reading4:</b> {{i.reading_4}}<br>
                        {% endif %}
                        {% if i.reading_5 %}
                        <b>reading5:</b> {{i.reading_5}}<br>
                        {% endif %}
                        {% if i.reading_6 %}
                        <b>reading6:</b> {{i.reading_6}}<br>
                        {% endif %}
                        {% if i.reading_7 %}
                        <b>reading7:</b> {{i.reading_7}}<br>
                        {% endif %}
                        {% if i.reading_8 %}
                        <b>reading8:</b> {{i.reading_8}}<br>
                        {% endif %}
                        {% if i.reading_9 %}
                        <b>reading9:</b> {{i.reading_9}}<br>
                        {% endif %}
                        {% if i.reading_10 %}
                        <b>reading10:</b> {{i.reading_10}}<br>
                        {% endif %}
                    {% elif i.reading_value %}
                        {{i.reading_value}}
                    {% else %}
                        {{i.parameter_value}}
                    {% endif %}
                </td>
                <td>
                    {% if i.status %}
                        {{i.status}}
                    {% endif %}
                </td>
            </tr>
        {% endfor %}

    </table>
    <table>
        <tr>
            <td style="width: 80%;">
                <b>
                    The product confirms to the specification and is of standard quality.
                </b>
            </td>
        </tr>
    </table>
</div>

<br>
<br>
 <table>
        <tr>
            <td>
                Tested By<br>
                {% if doc.inspected_by_1 %}
                    {{ frappe.get_fullname(doc.inspected_by_1) or "" }}
                {% endif %}

            </td>
             <td>
                Prepared By<br>
                {% if doc.inspected %}
                    {{ frappe.get_fullname(doc.inspected) or "" }}
                {% endif %}

            </td>
            <td>
                Verified By<br>
                {% if doc.docstatus == 1 %}
                    {% if doc.approved_by %}
                        {{ frappe.get_fullname(doc.approved_by) or "" }}
                    {% endif %}
                {% endif %}

            </td>
        </tr>
    </table>
    <b>
                    This is a Computer generated document. No seal & signature is required.
                </b>
